INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('1e2dbc4c45344600a7cf0f1ae705ed18', 1619005158075, 'LA00001', 1619005158075, 1, '警情受理', '询问建筑倒坍情况、周边水源情况、着火部位的数量和位置等内容。', '地震、战时等大话务量情况下火灾', '10000', '19000', NULL, NULL, '30000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('38839eafbb60480ebe6b44066dfaa6c3', 1619005158075, 'LA00001', 1619005158075, 1, '警情受理', '询问火焰、烟雾的颜色、形态和浓密成度等情况，建筑结构，产所类型，脚手架情况，总建筑高度和楼层数，是否为高层建筑，着火楼层跨度，燃烧面积，毗邻建筑情况等内容。', '改建工程火灾', '10000', '19000', NULL, NULL, '30000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('ec50c87050a04d7fa067d9d8b3316876', 1619005158075, 'LA00001', 1619005158075, 1, '警情受理', '询问室外的供电线路及附件、零星垃圾、秸秆、草坪、树木、绿化带、芦苇、麦秆或大棚等燃烧物质种类和数量，燃烧面积，是否对周边建筑造成威胁，影响道路交通等内容。', '室外杂物火灾', '10000', '19000', NULL, NULL, '30000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('d03f8925288c444aa8cec28ef5350196', 1619005158075, 'LA00001', 1619005158075, 1, '警情受理', '在森林发生火灾时，询问森林火灾类型，气象条件，地势条件，森林面积，燃烧面积和长度，周边水源情况，附近建筑分布情况等内容。在草原发生火灾时，询问森林草原种类，气象条件，地势条件，草原面积，燃烧面积和长度，周边水源情况，附近建筑分布情况等内容。', '森林、草原火灾', '10000', '19000', NULL, NULL, '30000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('f48b54b8bb5c40d58000ff8e4c77a3da', 1619005158075, 'LA00001', 1619005158075, 1, '警情受理', '询问燃烧部位、形式及范围，燃烧物质、储量及工艺流程，燃烧物质及燃烧产物是否有毒，可燃气体（液体）泄露情况，燃烧罐内油品的性质（轻质或重质），储量，液面高度，立式、卧式、球式、地下、地上、半地下、拱顶、浮顶、金属、非金属等燃烧罐或装置情况，（再次）爆炸危险性等内容', '石油化工火灾', '10000', '19000', NULL, NULL, '30000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('9d5a971f90c046e4966c3c8d373cbdce', 1619005158075, 'LA00001', 1619005158075, 1, '行为规范', '精神饱满，情绪平稳。树立宗旨意识，坚持服务理念，在接警中做到精神饱满，以为报警人解决急难险警情为出发点。上岗前，调整情绪，摒弃杂念，不把个人情绪带到接警中。接听电话时，集中注意力，保持平静心态，不受报警人情绪影响。', NULL, NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('07d1e12caf2b4a0f98bb0bc3eb13ec39', 1619005158075, 'LA00001', 1619005158075, 1, '行为规范', '态度热情，语言文明。接警员在接警时要态度热情，用语规范、文明，尽可能用普通话。接警时要用礼貌用语开场，警情询问完，要主动告知受理结果，非当事人报警、提供建议（意见）的要表达谢意。接警过程，要使报警人感到接警员亲切、热情、专业。', NULL, NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('294f89c656404829bd61903dbc6edfb0', 1619005158075, 'LA00001', 1619005158075, 1, '行为规范', '积极引导，适当安抚。接到群众的紧急求助电话，要主动耐心引导报警人讲清案由、事件、地点等关键要素，询问时“请”字在先。报警人因焦急、紧张一时难以正确表述警情或多次、重复报警的，要给予适当安抚，缓解情绪，以较快速度问明警情和处理。', NULL, NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('1b9060553f874722882ac889ec8c1a6e', 1619005158075, 'LA00001', 1619005158075, 1, '禁语', '想好了再来问我！', '禁用语', NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('d028262f19db46d1a578e3f73ba5d8da', 1619005158075, 'LA00001', 1619005158075, 1, '行为规范', '业务熟练，询问简明。熟练掌握119接处警业务，对新出现的情况、或难以处置的警情迅速报告，以免延误处置，同时做好自身业务积累。在询问中做到用语准确、简练，切中关键，不随意打断报警人。对出现报警人无法准确表述的，不得训斥，更不得刁难。', NULL, NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('c48c5d1fdd984f83b6040197ff981115', 1619005158075, 'LA00001', 1619005158075, 1, '禁止情形', '严禁拒绝、推诿、扯皮现象。', NULL, NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('65e8ea88b39e484cac317d287dcb7766', 1619005158075, 'LA00001', 1619005158075, 1, '禁止情形', '严禁态度冷漠，语气生硬，情绪失控与报警人争吵。', NULL, NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('31268b0092e44da5ad22cb3092a8ce97', 1619005158075, 'LA00001', 1619005158075, 1, '禁止情形', '严禁长时间不应答或无故挂机。', NULL, NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('5131b984079c4604a97040706b64e87f', 1619005158075, 'LA00001', 1619005158075, 1, '禁止情形', '严禁答非所问，不耐烦、急躁，催促报警人。', NULL, NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('6b25d5fc25a148c0a33890e8e5c9c84f', 1619005158075, 'LA00001', 1619005158075, 1, '禁止情形', '严禁出现辱骂、呵斥等不文明语言。', NULL, NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('3218b6310e224c8082d17cd3ec502f33', 1619005158075, 'LA00001', 1619005158075, 1, '语音标准', '语气：礼貌、诚恳、亲和，体现出专业风格；', NULL, NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('57ff7ca9f96a4bdd9e109f35410a11d8', 1619005158075, 'LA00001', 1619005158075, 1, '语音标准', '音量：音量适中、悦耳，不宜过于大声；', NULL, NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('865b67e157dc466798e89ae426867bac', 1619005158075, 'LA00001', 1619005158075, 1, '语音标准', '语速：语速适中，每分钟吐字约120－150个；', NULL, NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('30b3319dfe8549d4b173df0da3ba07f1', 1619005158075, 'LA00001', 1619005158075, 1, '语音标准', '声调：自然、清晰、柔和，多用升调。', NULL, NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('95ec54111f124261bb303455627b5a7f', 1619005158075, 'LA00001', 1619005158075, 1, '应对话术', '电话接起两秒后，“您好，119，请讲”。', '首接语', NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('7786eca06d5e48e1a32392b2287ba815', 1619005158075, 'LA00001', 1619005158075, 1, '应对话术', '重复，“您好， 119，请讲”', '电话接通报警人无应答', NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('d37d5a4c6be040f288aad8001f709a81', 1619005158075, 'LA00001', 1619005158075, 1, '应对话术', '间隔3秒左右，继续提示报警人：“您好！您的电话已接通。”', '电话接通报警人无应答', NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('0157c4e8e4bb42d3a3759248f4f2ff11', 1619005158075, 'LA00001', 1619005158075, 1, '应对话术', '如果仍听不到回应，可告诉报警人：“很抱歉！我无法听见您的声音，请您重新拨打，再见！” 停顿2秒，然后挂机。', '电话接通报警人无应答', NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('17ec3f1d14204f27a5c9c8e03d560b27', 1619005158075, 'LA00001', 1619005158075, 1, '应对话术', '重复，“您好，119，请讲”', '若报警人电话里只顾自己说话或争吵', NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('ff2f8ef5457344499eafac491b6cd53a', 1619005158075, 'LA00001', 1619005158075, 1, '警情询问', '告知报警人“您别急，消防车辆已派出，我现在所问问题不影响消防车辆过来，请您配合一下！”、“同志，您现在听我说，请您别着急，您把地址告诉我，我才能把消防车派过来！”等，不断重复，直至稳定住报警人情绪。', '安抚与疏导', NULL, NULL, NULL, NULL, '20000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('de3b1695ef3640feaba36dc583ede5ad', 1619005158075, 'LA00001', 1619005158075, 1, '警情询问', '当常规的安抚语言无法引导报警人按照正常思路进行问答，那么接警员应当果断震慑对方，增强语言压制力，占据话语的主动权，让报警人跟着接警员的思路走，做到一问一答，直至顺利完成受理工作。', '打断与压制', NULL, NULL, NULL, NULL, '20000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('6d26f8b129084f6ea2d7ca4078b09eae', 1619005158075, 'LA00001', 1619005158075, 1, '警情询问', '接警员要及时准确地将报警内容输入到计算机中或填写到接警单上，并启动录音录时等相关设备。记录内容要简明扼要、完整规范。', '记录与提示', NULL, NULL, NULL, NULL, '20000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('f512d0911b1e47fbaf071af4df414596', 1619005158075, 'LA00001', 1619005158075, 1, '应对话术', '间隔3秒左右，继续提示报警人：“您好！您的电话已接通。”', '若报警人电话里只顾自己说话或争吵', NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('bb65d92553b6497fb1966d7ac73fe186', 1619005158075, 'LA00001', 1619005158075, 1, '应对话术', '如果仍听不到回应，可告诉报警人：“很抱歉！我无法收到您的回应，我们会给您回拨电话，再见！” 停顿2秒，然后挂机。', '若报警人电话里只顾自己说话或争吵', NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('50a800bb71504776ac148d61ccb67271', 1619005158075, 'LA00001', 1619005158075, 1, '禁语', '明明就是你不对！', '禁用语', NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('a6ba705884e84c2ebc54c85c037c2a37', 1619005158075, 'LA00001', 1619005158075, 1, '应对话术', '挂机后，回拨报警人电话，如果能够接警，就转入正常接警。回拨后，电话可以接通，但无人应答，且没什么特殊声音，可以直接挂断；如有特殊声音，可转110，并加强情况的跟踪、跟进。', '若报警人电话里只顾自己说话或争吵', NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('043da69bc5a74b1989e7181980993e74', 1619005158075, 'LA00001', 1619005158075, 1, '应对话术', '如果电话机的音量已调到最大，仍然听不清时，可以提醒报警人：“不好意思，我听不清您的声音，麻烦您再大声一些好吗？”', '报警人声音太小/周围环境太吵/手机信号弱，听不清楚', NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('3d8af300cc0443c0a3fb85a71cc91d47', 1619005158075, 'LA00001', 1619005158075, 1, '应对话术', '如果信号弱，听不清楚：“您的手机信号不太好，我听不清楚您的声音，请您变动一下位置。”', '报警人声音太小/周围环境太吵/手机信号弱，听不清楚', NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('f6f7e98c876b44198bb391729532267c', 1619005158075, 'LA00001', 1619005158075, 1, '应对话术', '如果确实无法听清：“很抱歉，我实在无法听清您说话，麻烦您换一部电话/换个安静的地方再拨，可以吗？”', '报警人声音太小/周围环境太吵/手机信号弱，听不清楚', NULL, NULL, NULL, NULL, '10000', NULL);
INSERT INTO "jcj_ts"("id", "cjsj", "czz", "gxsj", "yxx", "tslx", "tsnr", "tsgjz", "ajdl", "ajlx", "cdlx", "ajdj", "gflx", "px") VALUES ('1bd3db7925f64a9a99b5ed64bcbff0ba', 1619005158075, 'LA00001', 1619005158075, 1, '应对话术', '如果仍听不到报警人的回应，可告诉报警人：“很抱歉！我还是无法听清您的声音，您可以使用110短信平台报警，将报警内容发送至12110574，再见！” 停顿2秒，然后挂机，并通告110指挥中心，跟进相关情况。', '报警人声音太小/周围环境太吵/手机信号弱，听不清楚', NULL, NULL, NULL, NULL, '10000', NULL);
